package day3;

public class ex10_for3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*1부터 10사이의 짝수를 출력하는 코드를 작성하세요
		 * 
		 */
		int i=1;
		for( i=1 ; i<11 ; i++ ) {
			if(i%2==0) {
				System.out.println(i);
			}
		}
		
		for( i=1 ; i<6 ; i++) {
			System.out.println(2*i);
	
		}
	}
	
}
